                <script data-skip-moving="true">
                    (function() {
                        var isAmPmMode = false;
                        var time = document.getElementById("timeman-timer");
                        var hours = new Date().getHours();
                        var minutes = new Date().getMinutes();
                        if (time)
                        {
                            time.innerHTML = formatTime(hours, minutes, 0, isAmPmMode);
                        }
                        else if (document.addEventListener)
                        {
                            document.addEventListener("DOMContentLoaded", function() {
                                time.innerHTML = formatTime(hours, minutes, 0, isAmPmMode);
                            });
                        }
                        function formatTime(hours, minutes, seconds, isAmPmMode)
                        {
                            var ampm = "";
                            if (isAmPmMode)
                            {
                                ampm = hours >= 12 ? "PM" : "AM";
                                ampm = '<span class="time-am-pm">' + ampm + '</span>';
                                hours = hours % 12;
                                hours = hours ? hours : 12;
                            }
                            else
                            {
                                hours = hours < 10 ? "0" + hours : hours;
                            }
                            return    '<span class="time-hours">' + hours + '</span>' +
                                '<span class="time-semicolon">:</span>' +
                                '<span class="time-minutes">' + (minutes < 10 ? "0" + minutes : minutes) +
                                '</span>' + ampm;
                        }
                    })();
